import React, { Component } from 'react'
import M from 'materialize-css'

class AddTodo extends Component {
    state = {
        newTodo:''
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]:e.target.value
        })
    }

    render() {
        return (
            <div className='container'>
                <h3 className="center-align">Add New Todo</h3>
                <div className="input-field">
                    <input onChange={this.handleChange} type="text" id="newTodo"/>
                    <label htmlFor="new">Enter New Todo</label>
                    <a className="btn indigo center lighten-1">
                        <span>Submit</span>
                        <i className="material-icons indigo-text text-darken-4 right">submit</i>
                    </a>
                </div>
            </div>
        )
    }
}

export default AddTodo
